mapper.select:
SELECT a.* FROM t_law a
WHERE a.delete_flag=0

	and title like concat('%', #{title}, '%')

ORDER BY ${sort} ${order}
limit #{start},#{rows}

mapper.update:
UPDATE t_law 
SET 
	title = #{title},
	content = #{content}
WHERE  id = #{id}

UPDATE t_law
SET delete_flag = 1
WHERE  id = #{id}
